				timeTotalTXT.x = 355;
				timeTotalTXT.y = 300;
				timeTotalTXT.text = "זמן: " + minutes.toString() + ":" + printSec;
				timeTotalTXT.font = "20px Arial";
				timeTotal.textAlign = "center";
				timeTotalTXT.name = "timeTXT";
		
				var playAgain = new lib.buttonGo1();
